import React from 'react';
import Users from './Users';
class App extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            user: [
                {
                    id: 1,
                    name: "Andrey",
                    isСame: true,
                },
                {
                    id: 2,
                    name: "Nikita",
                    isСame: false,
                },
                {
                    id: 3,
                    name: "Vlad",
                    isСame: false,
                },
                {
                    id: 4,
                    name: "Oleg",
                    isСame: true,
                }
            ]
        }
    }
    render(){
        return(
            <div className="block">
                <div className="header">
                    <h1>Список людей на мероприятии</h1>
                </div>
                <Users user={this.state.user}/>
            </div>
        )
    }
}

export default App;
