import React from 'react';

class AddUser extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            user: [
                {
                    id: 1,
                    name: "",
                    isCame: false,
                }
            ]
        }
    }
    render(){
        return(
            <form>
                <input placeholder='Введите имя пользователя: ' onChange={(e)=>({name: this.setState(e.target.value)})}/>
                <label htmlFor="isCame">Присутствует ?</label>
                <input type="checkbox" id="isCame" onChange={(e)=>(this.setState({isCame: e.target.checked}))} />
                <button type="button" onClick={
                    console.log(this.state.name + " " + this.state.isCame)
                }>Отметить</button> 
            </form>
        )
    }
}

export default AddUser;
